// Calibration function for Air Quality (CO‚ÇÇ ppm)
function calibrateAirQuality(raw_value) {
    let raw_min = 100;
    let raw_max = 800;
    let ppm_min = 400;
    let ppm_max = 2000;
    let calibrated = ppm_min + ((raw_value - raw_min) / (raw_max - raw_min)) * (ppm_max - ppm_min);
    return Math.round(Math.max(ppm_min, Math.min(calibrated, ppm_max)) * 100) / 100; // round to 2 decimals
}

